package video7;

public class Operadores {

	public Operadores() {
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 3;
		x = x + 3;
		System.out.println(x);
		x = x - 3;
		System.out.println(x);
		x+=3;
		System.out.println(x);
		x-=3;
		System.out.println(x);
		x/=3;
		System.out.println(x);
		x++;
		System.out.println(x);
		x--;
		System.out.println(x);
		++x;
		System.out.println(x);
		--x;
		System.out.println(x);
	}

}
